import React from "react"
import '../../assets/css/Busquedas.css';

import Container from 'react-bootstrap/Container'
import Card from 'react-bootstrap/Card'
import Nav from 'react-bootstrap/Nav'

import { Link, DirectLink, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'



class SearchResult extends React.Component{
  constructor(props) {
    super(props);
    this.scrollToTop = this.scrollToTop.bind(this);
  }

  componentDidMount() {
    Events.scrollEvent.register('begin', function () {
      console.log("begin", arguments);
    });

    Events.scrollEvent.register('end', function () {
      console.log("end", arguments);
    });

  }
  scrollToTop() {
    scroll.scrollToTop();
  }
  scrollTo() {
    scroller.scrollTo('scroll-to-element', {
      duration: 800,
      delay: 0,
      smooth: 'easeInOutQuart'
    })
  }
  scrollToWithContainer() {

    let goToContainer = new Promise((resolve, reject) => {

      Events.scrollEvent.register('end', () => {
        resolve();
        Events.scrollEvent.remove('end');
      });

      scroller.scrollTo('scroll-container', {
        duration: 800,
        delay: 0,
        smooth: 'easeInOutQuart'
      });

    });

    goToContainer.then(() =>
      scroller.scrollTo('scroll-container-second-element', {
        duration: 800,
        delay: 0,
        smooth: 'easeInOutQuart',
        containerId: 'scroll-container'
      }));
  }
  componentWillUnmount() {
    Events.scrollEvent.remove('begin');
    Events.scrollEvent.remove('end');
  }

  render(){
    return(
      <Container id='search_result_container'>
        <Card id='search_result_card'>
            <Card.Header > Resultados

            </Card.Header >
            <Card.Body id='card_body_result_container' >
            <Container name="test1" >
              test 1
            </Container>

            <Element name="test2" className="element">
              test 2
            </Element>

            <Element name="test3" className="element">
              test 3
            </Element>

            <Element name="test4" className="element">
              test 4
            </Element>

            <Element name="test5" className="element">
              test 5
            </Element>

            <div id="anchor" className="element">
              test 6 (anchor)
            </div>

            <Link activeClass="active" to="firstInsideContainer" spy={true} smooth={true} duration={250} containerId="containerElement" style={{ display: 'inline-block', margin: '20px' }}>
              Go to first element inside container
            </Link>

            <Link activeClass="active" to="secondInsideContainer" spy={true} smooth={true} duration={250} containerId="containerElement" style={{ display: 'inline-block', margin: '20px' }}>
              Go to second element inside container
            </Link>

            <Element name="test7" className="element" id="containerElement" style={{
              position: 'relative',
              height: '200px',
              overflow: 'scroll',
              marginBottom: '100px'
            }}>

              <Element name="firstInsideContainer" style={{
                marginBottom: '200px'
              }}>
                first element inside container
            </Element>

              <Element name="secondInsideContainer" style={{
                marginBottom: '200px'
              }}>
                second element inside container
            </Element>
            </Element>


            <Element id="same" className="element">
              Two links point to this
            </Element>


            <Element name="scroll-to-element" className="element">
              Scroll to element
            </Element>

            <Element className="element" id="scroll-container" style={{
              position: 'relative',
              height: '200px',
              overflow: 'scroll',
              marginBottom: '100px'
            }}>

              <Element name="scroll-container-first-element" style={{
                marginBottom: '200px'
              }}>
                first element inside container
            </Element>

              <Element name="scroll-container-second-element" style={{
                marginBottom: '200px'
              }}>
                second element inside container
            </Element>
            </Element>

            <a onClick={this.scrollToTop}>To the top!</a>
          </Card.Body>

        </Card>

      </Container>
    )
  }
}
export default SearchResult
