import React from "react"
import '../../assets/css/Busquedas.css'

import {Row, Col, Container, Card} from 'react-bootstrap'

class objeto_pregunta extends React.Component{
  constructor(props){
    super(props)

  }

  render(){
    return(
    <Card border="success" style={{ width: '18rem' }}>
      <Card.Header>pregunta num {this.props.numero}</Card.Header>
      <Card.Body>
        <Card.Title>Success Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the bulk
          of the card's content.
        </Card.Text>
      </Card.Body>
    </Card>
    )
  }
}


class ScrollerResultados extends React.Component{
  constructor(props){
    super(props)
    this.state={
      numero_preguntas_encontradas: 0
    }
  }

  refreshPreguntasEncontradas(){
    this.state.numero_preguntas_encontradas = this.props.estado.resultados.lenght
    console.log(this.props.estado.resultados.length)
  }

  componentDidUpdate(prevProps) {
    // Typical usage (don't forget to compare props):
    console.log(this.state.numero_preguntas_encontradas + ' ' + prevProps.numero_preguntas_encontradas);
    if (this.state.numero_preguntas_encontradas != prevProps.numero_preguntas_encontradas) {
      this.fetchData(this.state.numero_preguntas_encontradas);
    }
  }

  render(){
    return(
      <Card id='search_result_card'>
        <Card.Header id='card_header' > Resultados:  {this.state.numero_preguntas_encontradas}</Card.Header>
        <Card.Body id='card_body_result_container'>
          <Container fluid='true'>
            <Row top="xs">
              <Col id='search_result_container'>
              </Col>
            </Row>
          </Container>
        </Card.Body>
      </Card>
    )
  }
}

export default ScrollerResultados
